        &lt;div class=&quot;item-info-detail&quot;&gt;
          &lt;text class=&quot;title&quot;&gt;{{item.title}}&lt;/text&gt;
          &lt;div class=&quot;detail-info&quot;&gt;
            &lt;text class=&quot;desc&quot;&gt;{{item.desc}}&lt;/text&gt;
          &lt;/div&gt;
        &lt;/div&gt;
      &lt;/div&gt;
    &lt;/div&gt;
  &lt;/div&gt;
