        &lt;div class=&quot;item-content&quot;&gt;
          &lt;div class=&quot;item-imgbox&quot;&gt;
            &lt;img class=&quot;item-img&quot; src=&quot;{{item.img}}&quot; alt=&quot;&quot; /&gt;
