&lt;script&gt;
var dom = require(&#39;@weex-module/dom&#39;)

module.exports = {
  data: {
    rows: []
  },
  created: function () {
    for (var i = 0; i &lt; 100; i++) {
      this.rows.push({
        id: i
      })
    }
  },
  methods: {
    go: function () {
